import React, { useState, useEffect } from 'react';
import { Typewriter } from 'react-simple-typewriter';
import { motion } from 'framer-motion';

export default function Home() {
  const [scrollY, setScrollY] = useState(0);

  useEffect(() => {
    const handleScroll = () => setScrollY(window.scrollY);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <section className="min-h-screen flex items-center justify-center px-8 pt-20">
      <div className="max-w-5xl w-full">
        <div 
          className="transition-all duration-700"
          style={{ 
            opacity: 1 - scrollY / 500,
            transform: `translateY(${scrollY * 0.3}px)`
          }}
        >
          {/* Decorative dots */}
          <div className="mb-8 flex gap-3">
            {[...Array(5)].map((_, i) => (
              <div key={i} className="flex flex-col gap-3">
                {[...Array(5)].map((_, j) => (
                  <div 
                    key={j} 
                    className="w-2 h-2 rounded-full" 
                    style={{ backgroundColor: '#d4a5a5' }}
                  />
                ))}
              </div>
            ))}
          </div>
          <h1 className="text-7xl md:text-8xl font-bold mb-8 leading-tight" style={{ color: '#b8c5d6' }}>
            <Typewriter
              words={['Hi! I\'m Nicole.']}
              typeSpeed={80}
              deleteSpeed={0}
              cursor
            />
          </h1>
          <motion.div
            initial={{ opacity: 0, y: 40 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 2, ease: "easeOut" }}
            viewport={{ once: true }}
          >
            <p className="text-2xl md:text-3xl font-medium text-slate-300 max-w-3xl leading-relaxed">
              A Computer Science Student passionate about building technology that connects people and creates meaningful impact.
            </p>
          </motion.div>
        </div>
      </div>
    </section>
  );
}
