import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Mail, Linkedin, Github } from 'lucide-react';

export default function Layout({ children }) {
  const location = useLocation();
  const [isLoaded, setIsLoaded] = useState(false);

  const isActive = (path) => location.pathname === path;
  const isHome = location.pathname === '/';

  useEffect(() => {
    setIsLoaded(true);
  }, []);

  // Handle scroll to About section on home page
  const handleAboutClick = (e) => {
    if (isHome) {
      e.preventDefault();
      const aboutSection = document.getElementById('about');
      if (aboutSection) {
        aboutSection.scrollIntoView({ behavior: 'smooth' });
      }
    } else {
      // If not on home page, navigate to home then scroll
      window.location.href = '/#about';
    }
  };

  return (
    <div className="min-h-screen bg-slate-800 text-slate-200" style={{ fontFamily: 'system-ui, -apple-system, sans-serif' }}>
      {/* Navigation */}
      <nav className="fixed top-0 w-full bg-slate-800/95 backdrop-blur-sm z-50 border-b border-slate-700">
        <div className="max-w-5xl mx-auto px-8 py-6 flex justify-between items-center">
          <Link to="/" className="flex items-center gap-3 glow-text">
            <img 
              src="/profile.jpg" 
              alt="Nicole Sutedja"
              className={`w-10 h-10 rounded-full object-cover border-2 transition-all duration-1000 ${
                isLoaded ? 'translate-x-0 rotate-0' : 'translate-x-[200px] rotate-[720deg]'
              }`}
              style={{ borderColor: '#c9aed4' }}
            />
            <span className="text-xl font-semibold tracking-wide" style={{ color: '#ffd6d6ff' }}>
              Nicole Sutedja
            </span>
          </Link>
          <div className="flex gap-8 items-center">
            <Link 
              to="/" 
              className={`text-sm font-medium glow-text ${isActive('/') ? 'text-pink-300' : ''}`}
            >
              Home
            </Link>
            <a 
              href="#about"
              onClick={handleAboutClick}
              className="text-sm font-medium glow-text cursor-pointer"
            >
              About
            </a>
            <Link 
              to="/experience" 
              className={`text-sm font-medium glow-text ${isActive('/experience') ? 'text-pink-300' : ''}`}
            >
              Experience
            </Link>
            <Link 
              to="/contact" 
              className={`text-sm font-medium glow-text ${isActive('/contact') ? 'text-pink-300' : ''}`}
            >
              Contact
            </Link>
            <div className="flex gap-4 ml-4">
              <a href="mailto:nicolesutedja@gmail.com" className="icon-glow">
                <Mail size={18} />
              </a>
              <a href="https://linkedin.com/in/nicolesutedja" target="_blank" rel="noopener noreferrer" className="icon-glow">
                <Linkedin size={18} />
              </a>
              <a href="https://github.com/nicolesutedja" target="_blank" rel="noopener noreferrer" className="icon-glow">
                <Github size={18} />
              </a>
            </div>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <main>{children}</main>

      {/* Footer */}
      <footer className="py-12 px-8 border-t border-slate-700">
        <div className="max-w-5xl mx-auto text-center text-slate-400 text-sm font-medium">
          <p>Â© 2024 Nicole Sutedja. Designed with care.</p>
        </div>
      </footer>
    </div>
  );
}
